def create_session_with_retries():
    """T·∫°o session v·ªõi retry strategy - Fixed version"""
    session = requests.Session()
    
    # C·∫•u h√¨nh retry strategy v·ªõi parameter m·ªõi
    retry_strategy = Retry(
        total=3,  # T·ªëi ƒëa 3 l·∫ßn retry
        status_forcelist=[429, 500, 502, 503, 504],  # Retry v·ªõi c√°c status code n√†y
        allowed_methods=["HEAD", "GET", "OPTIONS"],  # Thay method_whitelist b·∫±ng allowed_methods
        backoff_factor=2,  # TƒÉng delay gi·ªØa c√°c retry
        respect_retry_after_header=True  # T√¥n tr·ªçng Retry-After header t·ª´ server
    )
    
    adapter = HTTPAdapter(
        max_retries=retry_strategy,
        pool_connections=10,  # S·ªë connection pools
        pool_maxsize=20       # Max connections per pool
    )
    
    session.mount("http://", adapter)
    session.mount("https://", adapter)
    
    return session


def get_report_online(from_date: str, to_date: str, agency_id: str = '6852c2f06d65221a70e5b26b') -> dict:
    """B√°o c√°o h·ªì s∆° KGG dossier online - Version t·ªëi ∆∞u v·ªõi retry v√† timeout cao"""
    
    if not check_token_expiry(token):
        print("üîÑ Vui l√≤ng c·∫≠p nh·∫≠t token m·ªõi!")
        return None
    
    # Format ng√†y t·ª± ƒë·ªông
    if 'T' not in from_date:
        from_date = f"{from_date}T00:00:00.0Z"
    if 'T' not in to_date:
        to_date = f"{to_date}T23:59:59.0Z"
    
    print(f"üìÖ Truy v·∫•n KGG Dossier: {from_date} -> {to_date}")
    print(f"üè¢ Agency: {agency_id}")
    
    session = create_session_with_retries()
    
    try:
        print("‚è≥ ƒêang g·ª≠i request... (c√≥ th·ªÉ m·∫•t v√†i ph√∫t)")
        start_time = time.time()
        
        response = session.get(
            'https://apidvc.angiang.gov.vn/pa/kgg-dossier-statistic/--kgg-dossier-report-online',
            headers={
                'Authorization': f'bearer {token}',
                'User-Agent': 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36',
                'Accept': 'application/json, text/plain, */*',
                'Accept-Encoding': 'gzip, deflate, br',
                'Connection': 'keep-alive'
            },
            params={
                'from-applied-date': from_date,
                'to-applied-date': to_date,
                'agency-id': agency_id,
                'agencyLevel0': '5f6b17984e1bd312a6f3ae4b',
                'agencyLevel1': '5f7dade4b80e603d5300dcc4',
                'agencyLevel2': '5f6b177a4e1bd312a6f3ae4a',
                'procedureLevel4': '62b529f524023d508ef38fc0',
                'procedureLevel3': '62b529c424023d508ef38fbd',
                'procedureLevel2': '62b52a0224023d508ef38fc1',
                'is-ignore-free-dossier': 'true'
            },
            timeout=(30, 180),  # Connect: 30s, Read: 180s (3 ph√∫t)
            stream=True  # Stream ƒë·ªÉ tr√°nh memory issues v·ªõi response l·ªõn
        )
        
        response.raise_for_status()
        
        # ƒêo th·ªùi gian response
        elapsed_time = time.time() - start_time
        print(f"‚úÖ Request th√†nh c√¥ng! Th·ªùi gian: {elapsed_time:.2f}s")
        
        return response.json()
        
    except requests.exceptions.Timeout as e:
        elapsed_time = time.time() - start_time
        print(f"‚è∞ Timeout sau {elapsed_time:.2f}s: {e}")
        print("üí° G·ª£i √Ω: Th·ª≠ thu nh·ªè kho·∫£ng th·ªùi gian ho·∫∑c li√™n h·ªá admin ƒë·ªÉ ki·ªÉm tra server")
        return None
        
    except requests.exceptions.ConnectionError as e:
        print(f"üåê L·ªói k·∫øt n·ªëi m·∫°ng: {e}")
        print("üí° Ki·ªÉm tra internet ho·∫∑c VPN n·∫øu c·∫ßn")
        return None
        
    except requests.exceptions.HTTPError as e:
        status_code = e.response.status_code
        if status_code == 401:
            print("üîê L·ªói 401: Token h·∫øt h·∫°n ho·∫∑c kh√¥ng h·ª£p l·ªá")
        elif status_code == 403:
            print("üö´ L·ªói 403: Kh√¥ng c√≥ quy·ªÅn truy c·∫≠p")
        elif status_code == 429:
            print("üö¶ L·ªói 429: Rate limit - ƒë·ª£i v√† th·ª≠ l·∫°i")
        elif status_code >= 500:
            print(f"üîß L·ªói server {status_code}: V·∫•n ƒë·ªÅ t·ª´ ph√≠a API")
        else:
            print(f"‚ùå HTTP Error {status_code}: {e}")
        return None
        
    except requests.exceptions.JSONDecodeError as e:
        print(f"üìÑ L·ªói parse JSON: {e}")
        print("üí° Response c√≥ th·ªÉ kh√¥ng ph·∫£i JSON ho·∫∑c b·ªã truncated")
        return None
        
    except Exception as e:
        print(f"‚ùå L·ªói kh√¥ng mong mu·ªën: {type(e).__name__}: {e}")
        return None
        
    finally:
        session.close()



